import React, { useState } from 'react';



const timelineData = [

    { date: "2020-01-01", title: "Event 1", description: "Description for event 1" },

    { date: "2021-05-15", title: "Event 2", description: "Description for event 2" },

    // ... more events

];



function CarouselTimeline() {

    const [activeIndex, setActiveIndex] = useState(0); 
    const handleNext = () => setActiveIndex(activeIndex + 1);

    const handlePrev = () => setActiveIndex(activeIndex - 1);
    return (

        <div className="timeline-container">

            <div className="timeline-track">

                {timelineData.map((event, index) => (

                    <TimelineItem 

                        key={index} 

                        event={event} 

                        isActive={index === activeIndex} 

                    />

                ))}

            </div>

            <button onClick={handlePrev}>Previous</button>

            <button onClick={handleNext}>Next</button>
        </div>
    );
}

function TimelineItem({ event, isActive }) {

    return (

        <div className={`timeline-item ${isActive ? 'active' : ''}`}>

            <div className="date">{event.date}</div>

            <div className="event-details">

                <h3>{event.title}</h3>

                <p>{event.description}</p>

            </div>

        </div>

    );

}

export default CarouselTimeline;
