import React, { useState } from 'react';
import './CarouselTimeline.css';

const timelineData = [

    { date: "2020-01-01", title: "Event 1", description: "Description for event 1" },

    { date: "2021-05-15", title: "Event 2", description: "Description for event 2" },

    { date: "2021-05-15", title: "Event 3", description: "Description for event 3" },

    // ... more events

];



function CarouselTimeline() {

    const [activeIndex, setActiveIndex] = useState(0); 
    const handleNext = () => {
        if (activeIndex < timelineData.length - 1) {
            setActiveIndex((prev) => prev + 1);
        }
    };

    const handlePrev = () => {
        if (activeIndex > 0) {
            setActiveIndex((prev) => prev - 1);
        }
    };
    return (

        <div className="timeline-container">

            <div className="timeline-track">

                {timelineData.map((event, index) => (

                    <TimelineItem 

                        key={index} 

                        event={event} 

                        isActive={index === activeIndex} 

                    />

                ))}

            </div>

            <div class = "buttons-class">
                <button 
                    className="arrow left-arrow" 
                    onClick={handlePrev} 
                    disabled={activeIndex === 0}
                >
                    &#8592; {/* Left Arrow Unicode */}
                </button>
                <button 
                    className="arrow right-arrow" 
                    onClick={handleNext} 
                    disabled={activeIndex === timelineData.length - 1}
                >
                    &#8594; {/* Right Arrow Unicode */}
                </button>
            </div>


        </div>
    );
}

function TimelineItem({ event, isActive }) {

    return (

        <div className={`timeline-item ${isActive ? 'active' : ''}`}>

            <div className="date">{event.date}</div>

            <div className="event-details">

                <h3>{event.title}</h3>

                <p>{event.description}</p>

            </div>

        </div>

    );

}

export default CarouselTimeline;
