import React, { useState } from 'react';
import './CarouselTimeline.css';

const timelineData = [
    {
        date: "Oct. 2024 - Current",
        title: "Software Engineer Intern",
        company: "Eric and Wendy Schmidt Center for Data Science & Environment (DSE)",
        description: [
            "I am developing a ",
            { text: "Python-based application", bold: true },
            " with interdisciplinary teams of postdocs and Indigenous biologists to facilitate ",
            { text: "wildlife image classification", bold: true }, " and support biodiversity efforts in Northern California through ",
            { text: "ETL pipelining", bold: true },
            " and programming ",
            { text: "computer vision frameworks.", bold: true } 
        ],
        languages: "Python",
        tools: "TKinter, Gitlab"
    },
    { 
        date: "Oct. 2024 - Current", 
        title: "LLM Researcher", 
        company: "Streets Labs", 
        description: ["This is a ", { text: "bold", bold: true }, " example description."] 
    },
    { date: "2021-05-15", title: "Event 3", company: "Description for event 3" },
    { date: "2022-03-10", title: "Event 4", company: "Description for event 4" },
    // Add more events as needed
];

function CarouselTimeline() {
    const [activeIndex, setActiveIndex] = useState(0);

    const handleNext = () => {
        if (activeIndex < timelineData.length - 1) {
            setActiveIndex((prev) => prev + 1);
        }
    };

    const handlePrev = () => {
        if (activeIndex > 0) {
            setActiveIndex((prev) => prev - 1);
        }
    };

    return (
        <div className="timeline-container">
            {/* Left Arrow */}
            <button 
                className="arrow left-arrow" 
                onClick={handlePrev} 
                disabled={activeIndex === 0}
            >
                &#8592;
            </button>

            {/* Timeline Track */}
            <div className="timeline-track">
                <div 
                    className="timeline-items"
                    style={{
                        transform: `translateX(calc(-100% * ${activeIndex}))`,
                        transition: 'transform 0.3s ease-in-out',
                    }}
                >
                    {timelineData.map((event, index) => (
                        <TimelineItem 
                            key={index} 
                            event={event} 
                            isActive={index === activeIndex} 
                        />
                    ))}
                </div>
            </div>

            {/* Right Arrow */}
            <button 
                className="arrow right-arrow" 
                onClick={handleNext} 
                disabled={activeIndex === timelineData.length - 1}
            >
                &#8594;
            </button>
        </div>
    );
}

function TimelineItem({ event, isActive }) {
    return (
        <div className={`timeline-item ${isActive ? 'active' : ''}`}>
            <div className="date">{event.date}</div>
            <div className="event-details">
                <h3>{event.title}</h3>
                <p>{event.company}</p>
                <div className="event-description">
                    {Array.isArray(event.description)
                        ? event.description.map((part, index) =>
                            typeof part === "string" ? (
                                <span key={index}>{part}</span>
                            ) : (
                                <span
                                    key={index}
                                    style={{
                                        fontWeight: part.bold ? "bold" : "normal"
                                    }}
                                >
                                    {part.text}
                                </span>
                            )
                        )
                        : event.description}
                </div>
                <div class = "cs-details">
                    <div class = "home-languages">Languages: {event.languages}</div>
                    <div class = "home-tools">Tools: {event.tools}</div>
                </div>

            </div>
        </div>
    );
}

export default CarouselTimeline;
