import React, { useState } from 'react';
import './CarouselTimeline.css';

const timelineData = [
    { date: "Oct. 2024 - Current", title: "Software Engineer Intern", company: "Eric and Wendy Schmidt Center for Data Science & Environment (DSE)", description: "I am developing a Python-based application with interdisciplinary teams of postdocs and Indigenous biologists to facilitate wildlife image classification in order to increase biodiversity in Northern California." },
    { date: "2021-05-15", title: "Event 2", company: "Description for event 2" },
    { date: "2021-05-15", title: "Event 3", company: "Description for event 3" },
    { date: "2022-03-10", title: "Event 4", company: "Description for event 4" },
    // Add more events as needed
];

function CarouselTimeline() {
    const [activeIndex, setActiveIndex] = useState(0);

    const handleNext = () => {
        if (activeIndex < timelineData.length - 1) {
            setActiveIndex((prev) => prev + 1);
        }
    };

    const handlePrev = () => {
        if (activeIndex > 0) {
            setActiveIndex((prev) => prev - 1);
        }
    };

    return (
        <div className="timeline-container">
            {/* Left Arrow */}
            <button 
                className="arrow left-arrow" 
                onClick={handlePrev} 
                disabled={activeIndex === 0}
            >
                &#8592;
            </button>

            {/* Timeline Track */}
            <div className="timeline-track">
                <div 
                    className="timeline-items"
                    style={{
                        transform: `translateX(calc(-100% * ${activeIndex}))`,
                        transition: 'transform 0.3s ease-in-out',
                    }}
                >
                    {timelineData.map((event, index) => (
                        <TimelineItem 
                            key={index} 
                            event={event} 
                            isActive={index === activeIndex} 
                        />
                    ))}
                </div>
            </div>

            {/* Right Arrow */}
            <button 
                className="arrow right-arrow" 
                onClick={handleNext} 
                disabled={activeIndex === timelineData.length - 1}
            >
                &#8594;
            </button>
        </div>
    );
}

function TimelineItem({ event, isActive }) {
    return (
        <div className={`timeline-item ${isActive ? 'active' : ''}`}>
            <div className="date">{event.date}</div>
            <div className="event-details">
                <h3>{event.title}</h3>
                <p>{event.company}</p>
                <div class = "event-description">
                    {event.description}
                </div>
            </div>
        </div>
    );
}

export default CarouselTimeline;
